import React, { Component } from 'react';
import ComponentsSidebar from '../shared/ComponentsSidebar';
import { Form, Button, Table } from 'react-bootstrap';
import Select from 'react-select';
import { useState, useEffect } from 'react';
import api from '../api';

export class FichePatient extends Component {

    render() {
        const [users, setUsers] = useState([]);
        const [isMounted, setIsMounted] = useState(false);

        useEffect(() => {
            !isMounted &&
                api.getUsers().then((json) => {
                    setUsers(json);
                    setIsMounted(true);
                });
        }, [isMounted]);
        return (
            <div>
                <div className="container d-flex p-md-0">
                    <ComponentsSidebar />
                    <ul>
                        {users && users.map((user, index) => {
                            return (
                                <li key={index}>
                                    {user.Title}
                                </li>
                            );
                        })}
                    </ul>
                    <div className="az-content-body pd-lg-l-40 d-flex flex-column">
                        <div className="az-content-breadcrumb">
                            <span>Registre</span>
                        </div>
                        <h2 className="az-content-title">Fiche de registre</h2>
                        <div className="row row-sm">
                            <div className="col-lg">
                                <p className="mg-b-10">Nom</p>
                                <Form.Control type="text" placeholder="Nom" />
                            </div>{/* col */}
                            <div className="col-lg">
                                <p className="mg-b-10">Prénom</p>
                                <Form.Control type="text" placeholder="Prénom" />
                            </div>{/* col */}
                            <div className="col-lg">
                                <p className="mg-b-10">Sexe</p>
                                <Form.Check type="radio" name="sexe" label="M" defaultChecked />
                                <Form.Check type="radio" name="sexe" label="F" />
                            </div>
                        </div>{/* row */}
                        <hr className="mg-y-10" />

                        <div className="row row-sm">
                            <div className="col-lg-4">
                                <p className="mg-b-10">Date de naissance</p>
                                <Form.Control type="date" placeholder="Prénom" />
                            </div>{/* col */}
                            <div className="col-lg-4">
                                <p className="mg-b-10">Contact</p>
                                <Form.Control type="text" placeholder="Contact" />
                            </div>{/* col */}
                            <div className="col-lg-4">
                                <p className="mg-b-10">Adresse</p>
                                <Form.Control type="text" placeholder="Adresse" />
                            </div>{/* col */}
                        </div>
                        <hr className="mg-y-10" />
                        <div className="row row-sm mg-b-20">
                            <div className="col-lg-6">
                                <p className="mg-b-10">Motif d'entrée</p>
                                <Select
                                    options={[
                                        { value: 'Ugrence', label: 'Urgence' },
                                        { value: 'CE', label: 'Consultation externe' },
                                        { value: 'RAD', label: 'Radiologie' }
                                    ]}
                                />
                            </div>{/* col-4 */}
                        </div>{/* row */}

                        <div className="row row-xs wd-xl-80p">
                            <div className="col-sm-6 col-md-3"><Button variant="primary btn-block">Primary</Button></div>
                        </div>
                        <hr />
                        <div className="az-content-label mg-b-5">Liste Globale</div>
                        <div className="table-responsive">
                            <Table hover className="mg-b-0">
                                <thead>
                                    <tr>
                                        <th>ID</th>
                                        <th>Name</th>
                                        <th>Position</th>
                                        <th>Salary</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    <tr>
                                        <th scope="row">1</th>
                                        <td>Tiger Nixon</td>
                                        <td>System Architect</td>
                                        <td>$320,800</td>
                                    </tr>
                                    <tr>
                                        <th scope="row">2</th>
                                        <td>Garrett Winters</td>
                                        <td>Accountant</td>
                                        <td>$170,750</td>
                                    </tr>
                                    <tr>
                                        <th scope="row">3</th>
                                        <td>Ashton Cox</td>
                                        <td>Junior Technical Author</td>
                                        <td>$86,000</td>
                                    </tr>
                                    <tr>
                                        <th scope="row">4</th>
                                        <td>Cedric Kelly</td>
                                        <td>Senior Javascript Developer</td>
                                        <td>$433,060</td>
                                    </tr>
                                    <tr>
                                        <th scope="row">5</th>
                                        <td>Airi Satou</td>
                                        <td>Accountant</td>
                                        <td>$162,700</td>
                                    </tr>
                                </tbody>
                            </Table>
                        </div>{/* table-responsive */}
                    </div>{/* az-content-body */}

                </div>{/* container */}
            </div >
        )
    }
}

export default FichePatient;
